import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch, Redirect} from "react-router-dom";
import Search from "./pages/Search";
import Saved from "./pages/Saved";
import Nav from "./components/Nav";
import PrivateRoute from "./components/PrivateRoute"
import Login from "./components/Login";
import AuthButton from "./components/AuthButton";
import SignUp from "./components/SignUp";
import auth from "./utils/auth";
import API from "./utils/API"
// import NoMatch from "./pages/NoMatch";
import "./App.css";

// make state with
// userName 



// if userName is empty 

class App extends Component {

  state = {
    redirectToReferrer: false,
    userName: "",
    password: "",
    isUser: Boolean
  };


  // login--------------------------
  handleInputChange = e => {
    // Getting the value and name of the input which triggered the change
    let value = e.target.value;
    const name = e.target.name;

    if (name === "password") {
      value = value.substring(0, 15);
    }

    console.log("name " + name)
    console.log("value " + value)
    // Updating the input's state
    this.setState({
      [name]: value
    });
  };

  login = (e) => {
    e.preventDefault();
    console.log(this.state)
    let userName = this.state.userName;
    let password = this.state.password;
    let userInfo = {userName: userName, password: password}
    if (!this.state.password || !this.state.userName) {
        alert("Please fill out form!");
      } else if (this.state.password.length < 6) {
        alert(
          `Password must be at least 6 charactors long.`
        );
      } else {
        console.log(userInfo)
        API.checkUserLogin(userInfo)

        .then(res => {
          console.log(res.data);
         (res.data === null) ? this.setState({isUser: false}) : this.setState({isUser: true});
         console.log(this.state)
         if (this.state.isUser === true) {
            console.log("true!!!!!!")
        auth.authenticate(() => {
            this.setState(() => ({
              redirectToReferrer: true,

            }));
          }, userName);
        // alert(`Hello ${this.state.firstName} ${this.state.lastName}`);
      } else {
          console.log("try to login again ro go to sign in ")
      }
        })

        .catch(err => console.log(err));
      console.log(this.state);

      }
  };

  render() {
    // const { from } = this.props.location.state || { from: { pathname: "/" } };
    const { redirectToReferrer } = this.state;

    // if (redirectToReferrer === true) {
    //   return <Redirect to={"/saved"} />;
    // }

    return (
      <div>
        <Router>
          <AuthButton />
          <div>
            <Nav />
            <Switch>
              <Route exact path="/" component={Search} />
              <PrivateRoute exact path="/saved" component={Saved} />
              <Route exact path="/login" component={Login} handleInputChange={this.handleInputChange} login={this.login}/>
              <Route exact path="/signup" component={SignUp} name="userName"
                   />
            </Switch>
          </div>
        </Router>

      </div>
    );
  }
}

export default App;
