import React, { Component } from "react";
import API from "../utils/API";
// import { Link } from "react-router-dom";

class Search extends Component {
    state = {
        books: [],
        title: "",
        authors: "",
        discription: "",
        image: "",
        link: ""
    };

    componentDidMount() {
        this.loadBooks();
    }

    loadBooks = () => {
        API.getBooks("pride prejudice") 
        .then(res => 
            this.setState({ books: res.data, title: "", title: "", authors: "", discription: "", image: "", link: ""})
        )
        .catch(err => console.log(err));
        console.log(this.state.books)
    }

    loadBooksEvent = (e) => {
        e.preventDefault();
        this.loadBooks();
    }


    render() {
        return (
            <div>
                <h1>Search Page</h1>
                <button onClick="this.loadBooks"></button>
            </div>
        )
    }
}

export default Search;
