import React, { Component } from "react";
import API from "../utils/API";
import SearchCard from "../components/SearchCard"
import SearchButton from "../components/SearchButton"
import SearchBar from "../components/SearchBar"
// import { Link } from "react-router-dom";
let query;

class Search extends Component {

    state = {
        books: [],
        title: "",
        authors: [],
        description: "",
        image: "",
        link: ""
    };



    // componentDidMount() {
    //     this.loadBooks();
    // }

    loadBooks = () => {
        API.getBooks(query) 
        .then(res => 
            this.setState({ books: res.data.items, title: "", authors: "", discription: "", image: "", link: ""})
        )
        .catch(err => console.log(err));
        console.log(this.state.books)
    }

    loadBooksEvent = (e) => {
        console.log("in here")
        e.preventDefault();
        this.loadBooks();
    }

    handleInputClick = e => {
        console.log("in here")
        query = e.target.value;
        console.log(query)
        this.loadBooksEvent(e);
    }

    // handleInputChange = event => {
    //     const { name, value } = event.target;
    //     this.setState({
    //       [name]: value
    //     });
    //   };

    // handleFormSubmit = event => {
    //     event.preventDefault();
    //     if (this.state.title && this.state.author) {
    //       API.saveBook({
    //         title: this.state.title,
    //         author: this.state.author,
    //         synopsis: this.state.synopsis
    //       })
    //         .then(res => this.loadBooks())
    //         .catch(err => console.log(err));
    //     }
    //   };


    render() {
        return (
            <div>
                <h1>Search Page</h1>
                <SearchBar >
                <SearchButton loadBooksEvent={this.loadBooksEvent} handleInputClick={this.handleInputClick} />
                </SearchBar >
                {this.state.books.map(book => (
                  <SearchCard 
                  id={book.id}
                  title={book.volumeInfo.title}
                  author={book.volumeInfo.authors}
                  description={book.volumeInfo.description}
                  image={book.volumeInfo.imageLinks.thumbnail}
                  link={book.saleInfo.buyLink}
                  />
                ))}

            </div>
        )
    }
}

export default Search;
